<?php use yii\helpers\Url;?><ul>	<?php	foreach($menus as $menu) {		?>		<li><a href="<?= Yii::$app->homeUrl.$menu['link'] ?>" ><?= $menu['name'] ?></a>		<?php if(isset($menu['child'])) {			?>				<ul>					<?php						foreach($menu['child'] as $submenus) {							?>							<li><a href="<?= Yii::$app->homeUrl.'category/'.$submenus['link'] ?>" ><?= $submenus['name'] ?></a>							<?php if(isset($submenus['child'])) {								?>								<ul>									<?php									foreach ($submenus['child'] as $submenuchild) {										?>											<li><a href="<?= Yii::$app->homeUrl.$submenuchild['link'] ?>" ><?= $submenuchild['name'] ?></a>										<?php									}									?>								</ul>								<?php							}						}					?>				</ul>			<?php		}	}	?></ul>